function randomIntFromInterval(min, max) {


    return Math.floor(Math.random() \* (max - min + 1) + min)


}


function diceRoll() {


    return randomIntFromInterval(1, 6);


}


const score = {


    passLine: {


        wins: 0,


        loses: 0,


    },


    noPassLine: {


        wins: 0,


        loses: 0,


    },


};


const pointOffRules = {


    passLine: {


        win: new Set([7, 11]),


        lose: new Set([2, 3, 12]),


    },


    noPassLine: {


        win: new Set([2, 3]), 


        lose: new Set([7, 11]),


    },


};


const pointOnRules = {


    passLine: {


        lose: new Set([7]),


    },


    noPassLine: {


        win: new Set([7]),


    },


};


function simulate(typeOfPlay) {


    let i = 0;


    const numberOfRuns = 10000000;


    while (i < numberOfRuns) {


        let pointOff = true;


        let rules = pointOffRules[typeOfPlay];


        let shouldBreak = false;


        while (!shouldBreak) {


            const result = diceRoll() + diceRoll();


            if (pointOff && typeOfPlay === "noPassLine" && result === 12) {


                continue;


            }


            if (rules.win.has(result)) {


                score[typeOfPlay].wins++;


                shouldBreak = true;


                break;


            } else if (rules.lose.has(result)) {


                score[typeOfPlay].loses++;


                shouldBreak = true;


                break;


            }


            if (pointOff) {


                pointOff = false;


                rules = {


                    ...pointOnRules[typeOfPlay],


                };


                if (typeOfPlay === 'passLine') {


                    rules = {


                        ...rules,


                        win: new Set([result]),


                    };


                } else {


                    rules = {


                        ...rules,


                        lose: new Set([result]),


                    };


                }


            }


        }


        i++;


    }


    const {wins, loses} = score[typeOfPlay];


    console.log(`${typeOfPlay}: ${(wins / (wins + loses)) \* 100.0}%`);


}


simulate('passLine')


simulate('noPassLine')
